CREATE TABLE `general-jishu` (
  `id` smallint(6) NOT NULL,
  `hit` int(255) NOT NULL COMMENT '阅读次数',
  `view` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` mediumtext COLLATE utf8_unicode_ci NOT NULL COMMENT '内容',
  `author` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT '作者',
  `laiyuan` varchar(30) COLLATE utf8_unicode_ci NOT NULL COMMENT '来源',
  `time` varchar(40) COLLATE utf8_unicode_ci NOT NULL COMMENT '时间',
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='新闻表';

--
-- 转存表中的数据 `general-jishu`
--

INSERT INTO `general-jishu` (`id`, `hit`, `view`, `title`, `content`, `author`, `laiyuan`, `time`, `picture`) VALUES
(1, 0, 1, '扬州市海棠国家林木种质资源库技术支撑情况介绍', '扬州市海棠国家林木种质资源库主要以南京林业大学林学院为技术依托单位，组建了海棠产业化研发专业人才队伍，其中高级职称8人，讲师3人，研究生20余人，在省部级相关科研项目（已经完成9项，正在进行中4项）的支持下，海棠基地已经具备品种引进、资源创新、科学研究等条件。|本项目涉及良种培育、良种推广、科技示范等诸多方面的内容，因此项目实施过程中，应重视科学技术的应用，经常性开展科学试验、将适用、成熟的科技成果，应用于建设和生产的全过程，使科技成果直接转化为生产力。结合项目实际需要，努力寻求与各科研部门合作的机会，对项目运行中存在的技术问题进行攻关，在实践中解决难题。同时要建立和完善技术服务和技术推广体系，及时做好项目建设与经营中的技服务工作，及时对项目管理和工程技术人员进行培训，特别是给一线的技术人员进行必要的深造学习机会，以提高项目建设者的整体素质。', '扬州市海棠国家林木种质资源库', '', '2017-07-05', ''),
(2, 0, 1, '马尾松种质资源库', '目前，马尾松种质资源库的技术支撑单位是南京林业大学，技术支撑专家是南京林业大学的王章荣教授和徐立安教授。|良种基地于1985年开始，在南京林业大学陈岳武教授等专家指导下合作开展了马尾松遗传改良工作，坚持三十年不间断持续合作。在合作过程中始终本着团结合作、互敬互谅的精神，发挥双方的技术优势、人才优势、信息优势和资源优势，合作领域不断拓展，取得较多的科技成果。今后我们将继续在王章荣、徐立安教授指导下，在马尾松种质资源遗传评价，马尾松高世代育种群体及生产群体建立，马尾松良种繁育家系化、无性系繁育优良家系化，马尾松种子园优质、高产、稳产等方面开展工作。', '福建省漳平市五一林场国家马尾松种质资源库', '', '2017-06-16', ''),
(3, 0, 1, '长白县山楂海棠国家林木种质资源库技术支撑情况介绍', '长白县山楂海棠国家林木种质资源库，于2016年3月与吉林省林业科学研究院签署了《科技支撑协议书》，为资源库建设工作的开展奠定了坚实的基础。', '长白县山楂海棠国家林木种质资源库', '', '2017-06-08', ''),
(4, 1, 1, '云杉国家林木种质资源库', '白音敖包国家级自然保护区管理局与国家林业局森防总站、北京林业大学、内蒙古林科院、内蒙古农业大学、内蒙古大学等科研院所和高校保持长期的合作和科学研究方面的交流。并且由国家发展与改革委员会林木育种国家工程实验室作为科技支撑单位，提供科学指导和技术培训。', '克什克腾旗沙地云杉国家林木种质资源库', '', '2017-06-05', ''),
(5, 1, 1, '浆果经济树种国家林木种质资源库', '园区以黑河市林业科学院为技术依托单位，同时，与俄罗斯新西伯利亚中心植物园等8所俄罗斯科研单位，东北林业大学等10所国内科研院所建立了联系，签订科技合作协议，常年聘请10余位中俄林业专家到园区进行技术指导，创办每年一届的黑龙江黑河中俄林业生态建设学术论坛，开展学术交流，引进国外先进智力成果，提高园区科研能力和管理水平。现已具备了建立林木种质资源保护库的技术力量和条件。多年来，园区积极开展各项科学研究工作，先后承担和参与了20余项国家、省级林业科研和技术推广项目。其中《俄罗斯杨树良种繁育和示范推广》、《新西伯利亚花楸良种繁育和示范推广》等项目已结题，先后通过国家和省林业厅科技成果鉴定，《俄罗斯沙棘优良无性系引进与繁育技术研究》、《黑果腺肋花楸引种及栽培技术研究》等7项成果达到国内同类研究领先水平、6项目成果达到国内同类研究先进水平，分获黑龙江省林业厅科学技术进步壹、贰等奖，“俄罗斯蓝靛果忍冬引种繁育”课题获第五届梁希林业科学技术奖。|为把园区国家林木种质资源库建设好，聘用黑河市林科院、黑河市林木种苗站的科技人员及东北林业大学、黑龙江省森林植物园、黑龙江省农科院专家为园区顾问，在种质资源库的规划设计、建设管理给予技术支持，在技术上保证了国家林木种质资源库的发展建设。', '黑河市浆果经济树种国家林木种质资源库', '', '2017-05-21', '');

