--
-- 表的结构 `general-production`
--

CREATE TABLE `general-production` (
  `id` smallint(6) NOT NULL,
  `hit` int(255) NOT NULL COMMENT '阅读次数',
  `view` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` mediumtext COLLATE utf8_unicode_ci NOT NULL COMMENT '内容',
  `author` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT '作者',
  `laiyuan` varchar(30) COLLATE utf8_unicode_ci NOT NULL COMMENT '来源',
  `time` varchar(40) COLLATE utf8_unicode_ci NOT NULL COMMENT '时间',
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='新闻表';

--
-- 转存表中的数据 `general-production`
--

INSERT INTO `general-production` (`id`, `hit`, `view`, `title`, `content`, `author`, `laiyuan`, `time`, `picture`) VALUES
(1, 0, 1, '扬州市海棠国家林木种质资源库生产概况', '项目课题组在扬州市江都区现代花木园区内建立了国内最为丰富的海棠种质资源圃202亩，从国内外收集、保存了海棠种质205份，自主选育出优良海棠新品系58个，建立了海棠快繁和高效栽培技术体系，年产海棠苗木10万株，采穗圃每年生产海棠穗芽20万枚，繁殖海棠新品种苗木10万株以上。', '扬州市海棠国家林木种质资源库', '', '2017-07-05', ''),
(2, 0, 1, '资源库生产概况', '漳平五一国有林场现有经营面积28.3万亩,其中林业用地面积26.6万亩,占93.9％，有林地面积24.6万亩，竹林面积0.2万亩。用材林中属人工林 12.6万亩（其中马尾松6.4万亩、杉木5.7万亩、阔叶树0.6万亩）,天然林7.2万亩；（其中：人工幼林1.6万亩,中龄林3.2万亩,近成过熟林7.8万亩、蓄积量94.4万立方米；天然中龄林0.2万亩,近成过熟林6.9万亩、蓄积量53.8万立方米）。现已进入经营性采伐利用阶段，年产木材3万多立方米，具有较雄厚的经济基础，实现林分结构合理、永续利用的目标。|场部职能机构有办公室、营林股、生产股、销售股、计财股、种苗科研室、良种推广应用股、森保股、基建股、安全办。下设一个永福分场和三个经营区（梅水坑、官田、附城）；十五个工区（前坑、梅水坑、大杞、山羊、十三公里、官田、小村、潭露、福坪、龙潭、陈村、和丰、洪坑、双洋、永福），四十多个护林点以及一个独立核算的经济实体（林兴公司），三个木材销售楞场（大杞、厚福、大山）。', '福建省漳平市五一林场国家马尾松种质资源库', '', '2017-06-14', ''),
(3, 0, 1, '生产概况', '克什克腾旗沙地云杉国家林木种质资源保护库现有业务有房300㎡、辅助用房200㎡、生产用房760㎡（包括种子库260㎡、种子处理车间300㎡、办公用房200㎡）、晾晒场1000㎡。配备皮卡车、农用车、三轮车、摩托车等交通工具；项目区覆盖互联网络、移动网络，通讯非常便捷；现有仪器设备有：采种工具20套、检验设备10套、病虫害防治设备（喷雾器、喷药机、消毒器具等）10套、种子调制设备10套、自动气象站4台、监测分析系统4套。|近几年白音敖包国家级自然保护区收集保存沙地云杉种子16批，共13000斤。主要用于沙地云杉良种培育，累计生产苗木近6000多万株。', '克什克腾旗沙地云杉国家林木种质资源库', '', '2017-06-05', ''),
(4, 0, 1, '生产概况', ' 园区充分利用与俄相邻的地缘优势，收集保存适合高纬度寒冷地区生长栽培的植物资源，现已收集36科、77属、180余种植物，其中从俄罗斯新西伯利亚地区、阿尔泰边疆区、远东地区引进优良耐寒经济、绿化、用材树种60余种、品种200余个，先后建立了引进树种种质资源保存圃、采穗圃、科研试验区、种苗繁育区、栽培示范区等区域。|园区从俄罗斯和国内引进了欧洲花楸（Sorbus aucuparia）、西伯利亚花楸（Sorubus sibirica）、百花花楸（Sorbus  pohuashanensis）三个树种，保存份数：10000份，保存面积：6公顷。建立了花楸种质资源保存圃、标准化栽培示范园、苗木繁育区等，开展引种驯化和栽培技术研究，栽培苗木2万株，为种植资源保存和今后的育种工作提供丰富的种质资源。成功选育出了“冬红花楸”省级林木良种，繁育苗木500万株，推广栽培500公顷。|园区积极利用这些资源，开展各项科学研究工作，先后承担和参与了20余项国家、省级林业科研和技术推广项目，有13项课题通过科技成果鉴定，其中“俄罗斯蓝靛果忍冬引种繁育”课题获全国林业科技最高荣誉--第五届梁希林业科学技术奖，选育出了蓝靛果忍冬等6个林木良种，编制了《冬红花楸栽培技术规程》等四部省级行业标准。', '黑河市浆果经济树种国家林木种质资源库', '', '2017-05-21', '');

