--
-- 表的结构 `picture`
--

CREATE TABLE `picture` (
  `id` smallint(6) NOT NULL,
  `hit` int(255) NOT NULL COMMENT '阅读次数',
  `view` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` mediumtext COLLATE utf8_unicode_ci NOT NULL COMMENT '内容',
  `author` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT '作者',
  `laiyuan` varchar(30) COLLATE utf8_unicode_ci NOT NULL COMMENT '来源',
  `time` varchar(40) COLLATE utf8_unicode_ci NOT NULL COMMENT '时间',
  `picture_title` varchar(150) COLLATE utf8_unicode_ci NOT NULL,
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='新闻表';

--
-- 转存表中的数据 `picture`
--

INSERT INTO `picture` (`id`, `hit`, `view`, `title`, `content`, `author`, `laiyuan`, `time`, `picture_title`, `picture`) VALUES
(1, 47, 1, '三年桐', '三年桐，即油桐，也叫大戟科。落叶乔木。叶互生，叶卵形，或宽卵形，先端尖或渐尖，叶基心形，全缘或三浅裂。圆锥状聚伞花序顶生，花单性同株；花先叶开放，花瓣白，有淡红色条纹。核果球形，先端短尖，表面光滑。种子具厚壳状种皮。', '', '', '2017-10-1', '1111C0003102001664', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(2, 3, 1, '桑巨杨', '桑巨杨’是从植于新西兰的美洲黑杨优树上采下的天然杂交种，在北京播种、初选后，在河南焦作林业科学研究所进行同胞家系林选择，经品种比较林的比较而选育获得。', '', '', '2017-10-1', '1111C0003102001665', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(3, 2, 1, '千红椿', '千红椿是在河北廊坊市发现的变异臭椿品种。树冠紧凑，呈扁圆形；花序为单性雄花。', '', '', '2017-10-1', '1111C0003102001666', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(4, 0, 1, '埃瑞达诺', '埃瑞达诺，该品种于1955年由意大利杨树研究所利用人工控制授粉所获杂种，其母本为美洲黑杨，父本为采自日本东京青杨派马氏杨花粉，杂种人工无性系。', '', '', '2017-10-1', '1111C0003102001667', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(5, 0, 1, '金天使', '“金天使”是常绿灌木或小乔木，高1-5m，小枝黄色，稍四棱形，叶对生，椭圆形至倒卵形，大多长1.5-5cm，宽1.5-3.5cm，先端尖或钝，基部广契形，缘有细钝齿，两面无毛；有叶柄，叶脉明显。叶、芽、枝黄色，叶革质光亮半透明。', '', '', '2017-10-1', '1111C0003102001668', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(6, 0, 1, '沁盛香花槐', '沁盛香花槐是由全国绿色小康户郭锁胜通过愈伤组织培育成的新品种2009年7月国家林业局组织专家对该品种进行了实地审查，对品种的新颖性。', '', '', '2017-10-1', '1111C0003102001669', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(7, 0, 1, '赤霞', '赤霞’株形呈灌丛状，枝条半直立，当年生新枝上两轮红下绿，小枝四棱；叶片长6.1～6.8cm，宽2.9～3.8cm，叶片多椭圆形，颜色深绿，叶缘有起伏；花序平均长15cm，宽15cm，平均着花数65；着花密度（开花枝条数占总分枝数的百分比）100%；花径3.0～4.0cm，花色艳红，花瓣长1.0～1.5cm，宽0.9～1.2cm，花瓣卷曲且边缘褶皱，瓣爪艳红色，长0.8～1.1cm；花期6月下旬至9月底；果实椭圆形，成熟时褐色。‘赤霞’喜阳光充足和温暖的气候，宜栽植在通风向阳之处。', '', '', '2017-10-1', '1111C0003102001670', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(8, 6, 1, '中怀1号', '中怀1号苗期茎直立粗壮，灰褐色，具三条棱线；皮孔圆形-长形，短线形分布均匀；茎中部无毛具白条状蜡质；叶芽三角形，较小（0.5厘米长），芽距4厘米，芽绿色，先端钝，紧贴于茎。成年树主干通直圆满，树皮纵裂，裂痕浅；侧枝层次分明，圆形灰褐色，具明显棱痕，侧枝与主干夹角45o，较粗。', '', '', '2017-10-1', '1111C0003102001671', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(9, 12, 1, '雪中红', '‘雪中红’是国家林业局林业植物授权新品种，属于卫矛科卫矛属常绿灌木或小乔木；由北海道黄杨（冬青卫矛抗寒品种）选育而来，区别特征为冬季变为红色，故称“雪中红”，又称“冬红”；是北方少有的终年常绿阔叶木本园林植物，北方“唯一冬季变红的不落叶阔叶木本园林植物”，在北京等地区能生长良好，此新品种具有冬青卫矛（或大叶黄杨）的各项优点，抗污染抗寒抗旱耐贫瘠耐轻度盐碱等优点，是北方城市街道和小区的绿篱的首选植物，四季常青，冬季红色，极易管护。', '', '', '2017-10-1', '1111C0003102001672', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg'),
(10, 10, 1, '金臣', '‘金臣’多年生阔叶油料大乔木，自然生长的成年树可达高度20～30m；树形为主干茎分明的树冠扁平扩张状；树皮光滑呈浅灰色；叶序及叶型为互生叶，单叶，心形或具三或具五裂片，叶茎有一对杯状腺体；花色形态为单性、花瓣白色，雌、雄花共长一簇，同柄生长。果实为蒴果，先端尖锐，成熟后由绿色变成棕褐色；果皮较硬，形状如核桃，散生多数明显皱纹；种子为棕褐色，栗型，胚乳饱满，白色或金黄色。', '', '', '2017-10-1', '1111C0003102001673', '1.jpg|2.jpg|3.jpg|4.jpg|5.jpg|6.jpg|7.jpg');

