CREATE TABLE `ziyuan` (
  `id` int(100) NOT NULL,
  `view` int(11) NOT NULL COMMENT '审核',
  `hit` int(255) NOT NULL COMMENT '点击数',
  `number` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '平台编号',
  `bianhao` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '编号',
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '名字',
  `leixing` varchar(120) COLLATE utf8_unicode_ci NOT NULL COMMENT '种名',
  `english` varchar(120) COLLATE utf8_unicode_ci NOT NULL COMMENT '拉丁文',
  `shuming` varchar(120) COLLATE utf8_unicode_ci NOT NULL COMMENT '属名',
  `usefor` varchar(120) CHARACTER SET utf16 COLLATE utf16_unicode_ci NOT NULL COMMENT '用途',
  `xiangmu` varchar(120) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL COMMENT '关联项目',
  `danwei` varchar(120) COLLATE utf8_unicode_ci NOT NULL COMMENT '保存单位',
  `huzhao` text COLLATE utf8_unicode_ci NOT NULL COMMENT '护照信息',
  `base` text COLLATE utf8_unicode_ci NOT NULL COMMENT '基本信息',
  `share` text COLLATE utf8_unicode_ci NOT NULL COMMENT '共享信息',
  `other` text COLLATE utf8_unicode_ci NOT NULL COMMENT '其他',
  `picture` text COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

